mapper.select:
select jour.id as jourId,jour.journalTime,jour.orderNo,jour.receiveType,jour.postage,jour.totalAmount,jour.status, u.id as userId,u.username,u.phone as userPhone, jour.addrId,jour.province,jour.city,jour.area,jour.addrExtra as extra,jour.phone,jour.contacts, item.id,item.num,item.amount, model.id as modelId,model.commodityModel,model.commodityPrice, comm.id as commId,comm.commodityName,comm.commodityMainPic from ( select jour.*, addr.id as addrId,addr.province,addr.city, addr.area,addr.extra as addrExtra,addr.phone,addr.contacts from t_online_journal jour join t_recv_commodity_address addr on jour.recvCommodityAddressId = addr.id where 1=1
and ${queryCondition.whereCond}
order by ${pagination.orderByCols}
limit #{pagination.limitStart},#{pagination.limitNum}
) jour join t_user u on jour.userId = u.id join t_indent_details item on item.journalId = jour.id join t_online_commodity_model model on item.commodityModelId = model.id join t_online_commodity comm on model.commodityId = comm.id

select jour.id as jourId,jour.journalTime,jour.orderNo,jour.receiveType,jour.postage,jour.totalAmount,jour.status, u.id as userId,u.username,u.phone as userPhone, jour.addrId,jour.province,jour.city,jour.area,jour.addrExtra as extra,jour.phone,jour.contacts, item.id,item.num,item.amount, model.id as modelId,model.commodityModel,model.commodityPrice, comm.id as commId,comm.commodityName,comm.commodityMainPic from ( select jour.*, addr.id as addrId,addr.province,addr.city, addr.area,addr.extra as addrExtra,addr.phone,addr.contacts from t_online_journal jour join t_recv_commodity_address addr on jour.recvCommodityAddressId = addr.id where 1=1
and ${queryCondition.whereCond}
order by ${pagination.orderByCols}
limit #{pagination.limitStart},#{pagination.limitNum}
) jour join t_user u on jour.userId = u.id join t_indent_details item on item.journalId = jour.id join t_online_commodity_model model on item.commodityModelId = model.id join t_online_commodity comm on model.commodityId = comm.id
